import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const fetchWeather = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/weather?city=${city}`);
      setWeather(res.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('Could not fetch weather data.');
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: 'auto', padding: '2rem', fontFamily: 'Arial' }}>
      <h1>SmartWeather üå¶Ô∏è</h1>
      <input
        type="text"
        placeholder="Enter city..."
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ width: '100%', padding: '0.5rem' }}
      />
      <button onClick={fetchWeather} style={{ marginTop: '1rem', padding: '0.5rem', width: '100%' }}>
        Get Weather
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: '2rem' }}>
          <h2>{weather.name}</h2>
          <p>üå° Temp: {weather.main.temp} ¬∞C</p>
          <p>üíß Humidity: {weather.main.humidity}%</p>
          <p>üí® Wind: {weather.wind.speed} m/s</p>
          <p>üîç Condition: {weather.weather[0].description}</p>
        </div>
      )}
    </div>
  );
}

export default App;
