import React, { useState } from 'react';
import axios from 'axios';
import cities from './data/cities.json';

function App() {
  const [city, setCity] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const val = e.target.value;
    setCity(val);
    if (val.length > 1) {
      const match = cities.filter(c =>
        c.name.toLowerCase().startsWith(val.toLowerCase())
      );
      setSuggestions(match.slice(0, 10));
    } else {
      setSuggestions([]);
    }
  };

  const fetchWeatherByCoords = async (lat, lon) => {
    try {
      const [wRes, fRes] = await Promise.all([
        axios.get(`http://localhost:5000/api/weather?lat=${lat}&lon=${lon}`),
        axios.get(`http://localhost:5000/api/forecast?lat=${lat}&lon=${lon}`)
      ]);
      setWeather(wRes.data);
      setForecast(fRes.data.daily || []);
      setError('');
    } catch (err) {
      setError('Could not fetch weather data.');
      setWeather(null);
      setForecast([]);
    }
  };

  const fetchWeatherByLocation = () => {
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const { latitude, longitude } = pos.coords;
      fetchWeatherByCoords(latitude, longitude);
    }, () => {
      setError('Location access denied.');
    });
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '2rem', fontFamily: 'Arial' }}>
      <h1>SmartWeather üå¶Ô∏è</h1>

      <input
        type="text"
        placeholder="Enter city..."
        value={city}
        onChange={handleChange}
        style={{ width: '100%', padding: '0.5rem' }}
      />
      {suggestions.length > 0 && (
        <ul style={{ border: '1px solid #ccc', padding: 0, listStyle: 'none', maxHeight: '200px', overflowY: 'auto' }}>
          {suggestions.map((s, i) => (
            <li
              key={i}
              onClick={() => {
                setCity(s.name);
                setSuggestions([]);
                fetchWeatherByCoords(s.lat, s.lon);
              }}
              style={{ padding: '0.5rem', cursor: 'pointer', background: '#f9f9f9' }}
            >
              {s.name}
            </li>
          ))}
        </ul>
      )}

      <button onClick={fetchWeatherByLocation} style={{ marginTop: '1rem', padding: '0.5rem', width: '100%' }}>
        Use My Location
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: '2rem' }}>
          <h2>{weather.name}</h2>
          <p>üå° Temp: {weather.main.temp} ¬∞C</p>
          <p>üíß Humidity: {weather.main.humidity}%</p>
          <p>üí® Wind: {weather.wind.speed} m/s</p>
          <p>üîç Condition: {weather.weather[0].description}</p>
        </div>
      )}

      {forecast.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <h3>7-Day Forecast</h3>
          <ul style={{ padding: 0, listStyle: 'none' }}>
            {forecast.slice(0, 7).map((day, i) => (
              <li key={i} style={{ marginBottom: '1rem' }}>
                <strong>{new Date(day.dt * 1000).toLocaleDateString()}</strong><br />
                üå° {day.temp.day} ¬∞C - üíß {day.humidity}% - üí® {day.wind_speed} m/s<br />
                {day.weather[0].description}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
